package core.java.control;

public class SampleWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0;
		while (i <= 9) {
			System.out.println("The number is: " + i);
			i++;
		}
		System.out.println();
		
		int j =1;
		
		do {
			System.out.println("The number is: " + j);
			j++;
		}while(j<4);

	}

}
