package core.java.prac;

public class SampleOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
		//Arithmetic operator
		a += 6;
		System.out.println("A is: "+a);
		//Unary operator
		//--a;
		System.out.println("A is: " + a++);
		//++a;
		System.out.println("A is: "+ ++a);
		
		
	}

}
